package conditionsjava;

public class LogicalOrcondition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=30;
		int b=20;
		if (a==10 || b==20){
			System.out.println("either conditions are met");
		}
		else{
			System.out.println("conditions are not met");
		}
	}

}
